var db = require('controllers/database');
var mongoose = require('mongoose');
var papers = mongoose.model('papers');
var demands = mongoose.model('demands');
var users = mongoose.model('users');

exports.getPaper = function(country,callback) {
	papers.find({country:country},function(err,data){
		if (data.length != 0) {
      var dataPaper = [];
			for (i = 0; i < data.length; i++) {
				dataPaper.push({'_id':data[i]._id,'name':data[i].name,'place':data[i].place});
			}
			callback({'res':true,'data':dataPaper});
		} else {
			callback({'res':false});
		}
	});
}

exports.getPaperProfile = function(id,callback) {
	papers.find({_id:id},function(err,data){
		if (data.length != 0) {
			callback({'res':true,'data':data});
		} else {
			callback({'res':false});
		}
	});
}
